// Exemple de primitive Empiler
Procedure Empiler(Var P : ^Pile; x : Entier)
Var
    nouveau : ^Pile;
Debut
    nouveau := allouer(taille(Pile));
    nouveau^.val := x;
    nouveau^.suiv := P;
    P := nouveau;
Fin;
