sections.code:
Si (age >= 18) Alors
    Ecrire("Majeur");
Sinon
    Ecrire("Mineur");
Fin Si;

Algorithme ExemplesBoucles;
Var
    i, n, somme, note : Entier;
Debut
    // POUR: somme de 1 a n
    Lire(n);
    somme := 0;
    Pour i := 1 a n Faire
        somme := somme + i;
    Fin Pour;

    // TANTQUE: compte a rebours
    TantQue (n > 0) Faire
        Ecrire(n);
        n := n - 1;
    Fin TantQue;

    // REPETER: validation d'une note
    Repeter
        Ecrire("Donner une note entre 0 et 20:");
        Lire(note);
    Jusqua (note >= 0 Et note <= 20);
Fin.

Algorithme SwapSimple;
Var
    A, B, Temp : Entier;
Debut
    Lire(A);
    Lire(B);

    Temp := A;
    A := B;
    B := Temp;

    Ecrire("A=", A, " B=", B);
Fin.
