sections.code:
Algorithme ExempleStructure;
Const
    TVA = 0.19;
Var
    prixHT, prixTTC : Reel;
Debut
    Lire(prixHT);
    prixTTC := prixHT + prixHT * TVA;
    Ecrire("TTC = ", prixTTC);
Fin.

Const
    NMAX = 100;
    PI = 3.14159;
Var
    rayon : Reel;
    surface : Reel;
Debut
    Lire(rayon);
    surface := PI * rayon * rayon;
    Ecrire(surface);
Fin.

Var
    age : Entier;
    moyenne : Reel;
    lettre : Caractere;
    nomComplet : Chaine;
    estAdmis : Booleen;

Si (note >= 10) Et (absence = Faux) Alors
    Ecrire("Admis");
Sinon
    Ecrire("Non admis");
Fin Si;

Var
    i, somme : Entier;
Debut
    somme := 0;
    Pour i de 1 a 10 Faire
        somme := somme + i;
    Fin Pour;
    Ecrire(somme);
Fin.

Var
    x : Entier;
Debut
    Lire(x);
    Tant Que x < 100 Faire
        x := x * 2;
    Fin Tant Que;
    Ecrire(x);
Fin.

Var
    note : Entier;
Debut
    Repeter
        Ecrire("Donner une note entre 0 et 20:");
        Lire(note);
    Jusqua (note >= 0) Et (note <= 20);
Fin.

Algorithme BilanClasse;
Const
    SEUIL = 10;
Var
    i, n, nbAdmis : Entier;
    note : Reel;
Debut
    Lire(n);
    nbAdmis := 0;
    Pour i de 1 a n Faire
        Lire(note);
        Si note >= SEUIL Alors
            nbAdmis := nbAdmis + 1;
        Fin Si;
    Fin Pour;
    Ecrire("Admis: ", nbAdmis);
Fin.
