Var
    T[10] : Entier;
    M[3][4] : Reel;
    i, j : Entier;

// Parcours tableau
Pour i de 0 a 9 Faire
    Ecrire(T[i]);
Fin Pour;

// Parcours matrice
Pour i de 0 a 2 Faire
    Pour j de 0 a 3 Faire
        Ecrire(M[i][j]);
    Fin Pour;
Fin Pour;

Fonction RechercheSeq(T[100] : Entier; n, x : Entier) : Entier
Var
    i : Entier;
Debut
    Pour i de 0 a n-1 Faire
        Si T[i] = x Alors
            Retourner i;
        Fin Si;
    Fin Pour;
    Retourner -1;
Fin;

Fonction RechercheDicho(T[100] : Entier; n, x : Entier) : Entier
Var
    g, d, m : Entier;
Debut
    g := 0;
    d := n - 1;

    Tant Que g <= d Faire
        m := (g + d) div 2;
        Si T[m] = x Alors
            Retourner m;
        Sinon
            Si x < T[m] Alors
                d := m - 1;
            Sinon
                g := m + 1;
            Fin Si;
        Fin Si;
    Fin Tant Que;

    Retourner -1;
Fin;

Procedure TriSelection(Var T[100] : Entier; n : Entier)
Var
    i, j, imin, tmp : Entier;
Debut
    Pour i de 0 a n-2 Faire
        imin := i;
        Pour j de i+1 a n-1 Faire
            Si T[j] < T[imin] Alors
                imin := j;
            Fin Si;
        Fin Pour;

        tmp := T[i];
        T[i] := T[imin];
        T[imin] := tmp;
    Fin Pour;
Fin;

Procedure TriInsertion(Var T[100] : Entier; n : Entier)
Var
    i, j, cle : Entier;
Debut
    Pour i de 1 a n-1 Faire
        cle := T[i];
        j := i - 1;

        Tant Que (j >= 0) Et (T[j] > cle) Faire
            T[j+1] := T[j];
            j := j - 1;
        Fin Tant Que;

        T[j+1] := cle;
    Fin Pour;
Fin;

Procedure TriBulles(Var T[100] : Entier; n : Entier)
Var
    i, j, tmp : Entier;
    permute : Booleen;
Debut
    Pour i de 0 a n-2 Faire
        permute := Faux;
        Pour j de 0 a n-2-i Faire
            Si T[j] > T[j+1] Alors
                tmp := T[j];
                T[j] := T[j+1];
                T[j+1] := tmp;
                permute := Vrai;
            Fin Si;
        Fin Pour;

        Si permute = Faux Alors
            Retourner;
        Fin Si;
    Fin Pour;
Fin;
