Algorithme LireAfficherChaine;
Var
    nomComplet : Chaine;
Debut
    Ecrire("Entrez votre nom complet:");
    Lire(nomComplet);
    Ecrire("Bonjour ", nomComplet);
Fin.

Algorithme DemoFonctions;
Var
    a, b, c : Chaine;
Debut
    a := "Algo";
    b := "Compiler";
    c := Concat(a, b);
    Ecrire(c);
    Ecrire(Longueur(c));
Fin.

Algorithme ValidationNom;
Var
    nom : Chaine;
Debut
    Ecrire("Entrez un nom:");
    Lire(nom);

    Si Longueur(nom) = 0 Alors
        Ecrire("Nom vide");
    Sinon
        Ecrire("Nom valide: ", nom);
        Ecrire("Taille: ", Longueur(nom));
    Fin Si;
Fin.
