sections.code:
Type Maillon = Enregistrement
Debut
    val : Entier;
    suiv : ^Maillon;
Fin;

Algorithme InsertionTete;
Var
    tete, nouveau : ^Maillon;
Debut
    tete := NIL;
    nouveau := allouer(taille(Maillon));
    Si nouveau <> NIL Alors
        nouveau^.val := 10;
        nouveau^.suiv := tete;
        tete := nouveau;
    Fin Si;
Fin.
